import React from "react";

class App extends React.Component {
  state = {
    isLoding: true,
  };

  componentDidMount() {
    setTimeout(() => {
      this.setState({ isLoding: false });
    }, 3000);
  }
  render() {
    {
      const isLoding = this.state.isLoding;
    }
    return <div>{isLoding ? "Loding.." : "We are Ready!"}</div>;
  }
}
// this는 상위에 있는 선언한 객체를 불러온다고 생각하면된다. 여기서는 App을 뜻한다.
// 그래서 App.state.count라고 생각하면 된다.
// div안에 있어야 한다. (태그안에)
export default App;
