import React from 'react';

function Food({fav, color}) {
return (
      <div>
        <h3>Hello! I LUV {fav}</h3>
        <div>{fav} color is {color}</div>
      </div>
  )

}

const foodIlike = [
  {
    name: "kimchi",
    color: "red"
  },
  {
    name: "pizza",
    color: "yellow"
  },
  {
    name: "tomato",
    color: "red"
  }
];


function App() {
  return (
    <div>
      {foodIlike.map (food => {
        <Food fav={food.name} color={food.color} />
      })}
    <h2>ㅇㅇ</h2>
  </div>
  );
  }

// div안에 있어야 한다. (태그안에)
export default App;
