import React from "react";
import Axios from "axios";

class App extends React.Component {
  state = {
    isLoding: true,
    movies: [],
  };

  getMovie = async () => {
    const {
      data: {
        data: { movies },
      },
    } = await Axios.get("https://yts.mx/api/v2/list_movies.json");
    this.setState({ movies: movies, isLoding: false }); // movies:movies는 movies와 같다.
    console.log(movies); // 로딩을 완료한 후 we are ready!를 출력
  };

  componentDidMount() {
    this.getMovie();
  }
  render() {
    const { isLoding } = this.state.isLoding;
    return <div>{isLoding ? "Loding.." : "We Are Ready!"}</div>;
  }
}
// this는 상위에 있는 선언한 객체를 불러온다고 생각하면된다. 여기서는 App을 뜻한다.
// 그래서 App.state.count라고 생각하면 된다.
// div안에 있어야 한다. (태그안에)
export default App;
