import React from "react";
import PropTypes from "prop-types";

function Food({ fav, color, rating }) {
  return (
    <div>
      <h3>Hello! I LUV {fav}</h3>
      <div>
        <div>
          {fav} color is {color}
        </div>
        <p>my rating = {rating}/10</p>
      </div>
    </div>
  );
}

Food.propTypes = {
  fav: PropTypes.string.isRequired,
  color: PropTypes.string.isRequired,
  rating: PropTypes.number.isRequired,
};
// propTypes에는 Food 함수의 인자값을 키로 한다.

const foodIlike = [
  {
    id: 1,
    name: "kimchi",
    rating: 3,
    color: "red",
  },
  {
    id: 2,
    color: "yellow",
    name: "pizza",
    rating: 1,
  },
  {
    id: 3,
    name: "tomato",
    color: "red",
    rating: 7,
  },
];

function App() {
  return (
    <div>
      <h2>Hello</h2>
      {foodIlike.map((food) => (
        <Food
          key={food.id}
          fav={food.name}
          color={food.color}
          rating={food.rating}
        />
      ))}
    </div>
  );
}

// div안에 있어야 한다. (태그안에)
export default App;
